<?php	if (isset($_GET['success'])) {      if( $_GET['success'] == "true" ) {        echo "<script type='text/javascript'>alert('Registration Successful, returning to home page!')</script>";		echo "<script type='text/javascript'>window.location.href='../index.php'</script>";     } 	} elseif (isset($_GET['failed'])) {      if( $_GET['failed'] == "password" ) {        echo "<script type='text/javascript'>alert('Passwords do not match, please try again!')</script>";		echo "<script type='text/javascript'>window.location.href='register.php'</script>";	  } elseif($_GET['failed'] == "email" ) {        echo "<script type='text/javascript'>alert('Invalid email address, please try again!')</script>";		echo "<script type='text/javascript'>window.location.href='register.php'</script>";	    	  } elseif($_GET['failed'] == "database" ) {        echo "<script type='text/javascript'>alert('Database error, please see administrator!')</script>";		echo "<script type='text/javascript'>window.location.href='../index.php'</script>";	    	  }	}?><!DOCTYPE html><html>  <head>    <title>Spoonity Exam: Register</title>	<link href="../styles/css/bootstrap.css" rel="stylesheet">  </head>  <body>	<div class="content_holder">	<div class="form_holder">	<form class="form-horizontal" role="form" id="RequestForm" method="post" action="../scripts/registration.php" enctype="multipart/form-data">	<legend class="">User Details</legend>	  <div class="form-group">		<label for="RequestItem" class="col-sm-2 control-label">Email</label>		<div class="col-sm-10">		  <input type="text" class="form-control"  id="emailaddress" name="emailaddress" placeholder="Email Address" required>		</div>	  </div>	  <div class="form-group">		<label for="RequestItem" class="col-sm-2 control-label">Name</label>		<div class="col-sm-10">		  <input type="text" class="form-control" id="fullname" name="fullname" placeholder="Full Name">		</div>	  </div>  		<div class="form-group">		<label for=